import React, { Component } from "react";
import { Text, View } from "react-native";
import Header from "../../Component/Header/HeaderClose";
export default class Req extends Component {
  state = {
    data: "null"
  };

  handleSend = () => {
    console.log("handleSend");
    console.log(this.state);
    fetch("http://167.71.44.156:3001/api/getRequests", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: undefined
      // JSON.stringify({
      //     firstParam: "yourValue",
      //     secondParam: "yourOtherValue"
      //   })
    })
      .then(responseJson => {
        obj = JSON.parse(responseJson);
        this.state.data = obj;
      })
      .catch(error => {
        console.error(error);
      });
  };
  render() {
    return (
      <View>
        <Header onBackPress={() => this.props.navigation.navigate("Home")} />
        <View style={{ marginHorizontal: 15 }}>
          <Text
            style={{
              fontFamily: "OS_SB",
              fontSize: 22,
              textAlign: "justify",
              alignSelf: "center",
              marginBottom: 15
            }}
          >
            {this.state.data["data"][0]["id"]}
          </Text>
        </View>
      </View>
    );
  }
}
