import React, { Component } from "react";
import { Text, View } from "react-native";
import Header from "../../Component/Header/HeaderClose";
export default class Req extends Component {
  state = {
    data: "null"
  };

  handleSend() {
    console.log("handleSend");
    console.log(this.state.data);
    fetch("http://167.71.44.156:3001/api/getRequests", {
      method: "GET"
    })
      .then(response => response.json())
      .then(responseJson => {
        this.state.data = responseJson["data"];
      })
      .catch(error => {
        console.error(error);
      });
  }
  handleReq() {
    this.state.data.map((item, i) => {
      console.log("console", this.state.data[i]["name"]);
      return <Text>{this.state.data[1]["name"]}</Text>;
    });
  }

  componentWillMount() {
    this.handleSend();
  }
  render() {
    return (
      <View>
        <Header onBackPress={() => this.props.navigation.navigate("Home")} />
        <View style={{ marginHorizontal: 15 }}>
          <Text>hey1</Text>
          {/* {this.handleReq()} */}
          <Text
            style={{
              fontFamily: "OS_SB",
              fontSize: 22,
              textAlign: "justify",
              alignSelf: "center",
              marginBottom: 15
            }}
          >
            {this.state.data[0]["name"]}
            {this.state.data[0]["number"]}
          </Text>
        </View>
      </View>
    );
  }
}
