import React, { Component } from "react";
import { Text, View, StatusBar, TouchableOpacity, Image } from "react-native";
import Header from "../../Component/Header/HeaderClose";
import Card from "./card";
import { Tab, Tabs, ScrollableTab } from "native-base";
import { Drawer, Icon, Col } from "native-base";
import SideBar from "../../Screen/SideMenu/index";

export default class Req extends Component {
  state = {
    data: "null"
  };

  handleSend() {
    console.log("handleSend");
    console.log(this.state.data);
    fetch("http://167.71.44.156:3001/api/getRequests", {
      method: "GET"
    })
      .then(response => response.json())
      .then(responseJson => {
        this.setState({
          data: responseJson["data"]
        });
      })
      .catch(error => {
        console.error(error);
      });
  }

  handleReq() {
    console.log("handlereq ", this.state.data);
    var tifOptions = Object.keys(this.state.data).map(function(key) {
      return <Card value={key}>{this.state.data[key]}</Card>;
    });

    // const handle2 = this.state.data.map((item, i) => {
    //   console.log(i);
    // });
    // const handle = this.state.data.map((item, i) => {
    //   <Text>{this.state.data[i]["name"]}</Text>;
    //<Card data1={this.state.data[i]["name"]}></Card>;
    // });
    return tifOptions;
  }

  componentWillMount() {
    this.handleSend();
  }
  render() {
    console.log("render", this.state.data[0]);
    return (
      <Drawer
        ref={ref => {
          this.drawer = ref;
        }}
        content={
          <SideBar navigation={() => this.props.navigation.navigate("Home")} />
        }
        onClose={() => this.drawer._root.close()}
      >
        <StatusBar hidden={true} />
        <View style={{ flexDirection: "column", flex: 1 }}>
          <View
            style={{
              // position: "absolute",
              //flex: 1,
              flexDirection: "row",
              marginLeft: 15,
              ...Platform.select({
                ios: {
                  marginTop: 32
                },
                android: {
                  marginTop: 15
                }
              }),
              ...Platform.select({
                ios: {
                  zIndex: 9
                }
              })
            }}
          >
            <View style={{ flex: 0.1, justifyContent: "center" }}>
              <TouchableOpacity onPress={() => this.drawer._root.open()}>
                <Image
                  source={require("../../Image/menu.png")}
                  style={{
                    width: 15,
                    height: 15
                  }}
                />
              </TouchableOpacity>
            </View>
            <View style={{ flex: 0.7, justifyContent: "center" }}>
              <Image
                source={require("../../Image/harrier.png")}
                style={{ width: 70, height: 70, flex: 1, alignSelf: "center" }}
                resizeMode="contain"
              />
            </View>
            <View style={{ flex: 0.1, justifyContent: "center" }}>
              <TouchableOpacity
                onPress={() => this.props.navigation.navigate("ShoppingCart")}
              >
                <Image
                  source={require("../../Image/shopping_cart.png")}
                  style={{
                    width: 20,
                    height: 20
                  }}
                />
              </TouchableOpacity>
            </View>
            <View style={{ flex: 0.1, justifyContent: "center" }}>
              <TouchableOpacity
                onPress={() => this.props.navigation.navigate("Search")}
              >
                <Image
                  source={require("../../Image/search.png")}
                  style={{
                    width: 18,
                    height: 18
                  }}
                />
              </TouchableOpacity>
            </View>
          </View>
          <Tabs
            renderTabBar={() => (
              <ScrollableTab
                style={{ backgroundColor: "#fff" }}
                tabBarUnderlineStyle={{
                  backgroundColor: "#fff",
                  borderBottomWidth: 0
                }}
                underlineStyle={{
                  backgroundColor: Color.blue,
                  borderBottomWidth: 0
                }}
                tabContainerStyle={{
                  elevation: 0,
                  borderBottomColor: Color.white,
                  borderBottomWidth: 0,
                  marginTop: 30
                  //  width:400,
                  // marginHorizontal:0
                }}
              />
            )}
          >
            <View style={{ marginHorizontal: 15 }}>
              {Object.entries(this.state.data).map((t, k) => (
                <Card key={k} data1={this.state.data[k]}></Card>
              ))}
              <Text
                style={{
                  fontFamily: "OS_SB",
                  fontSize: 22,
                  textAlign: "justify",
                  alignSelf: "center",
                  marginBottom: 15
                }}
              >
                {/* {this.state.data[0]["name"]}
          {this.state.data[0]["number"]} */}
              </Text>
            </View>
          </Tabs>
        </View>
      </Drawer>
    );
  }
}
